import React, { useState } from 'react';
import { Link } from 'react-router-dom';

type Place = { id: number; name: string; description: string };
const places: Place[] = [
  { id: 1, name: 'Beach Paradise', description: 'A beautiful beach with white sands and blue water.' },
  { id: 2, name: 'Mountain Retreat', description: 'Escape into nature with hiking trails and fresh air.' },
  { id: 3, name: 'Historic City', description: 'Discover old town charm, museums, and great food.' },
];

function Explore() {
  const [selectedPlace, setSelectedPlace] = useState<Place | null>(null);

  const buttonStyle: React.CSSProperties = {
    position: 'fixed',
    top: '180px', // Position below the navigation buttons
    backgroundColor: '#ffffff',
    color: '#000',
    border: '2px solid black',
    borderRadius: '50%', // Makes it circular
    padding: '12px 16px',
    fontWeight: 'bold',
    cursor: 'pointer',
    textDecoration: 'none',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    width: '60px',
    height: '60px',
    fontSize: '14px',
    zIndex: 998,
  };

  return (
    <div style={{ position: 'relative' }}>
      {/* Go Tripping header with pink background */}
      <div style={{ 
        width: '100%', 
        backgroundColor: '#ff69b4',
        color: '#000',
        padding: '1.5rem',
        textAlign: 'center',
        position: 'fixed',
        top: 0,
        left: 0,
        zIndex: 1000
      }}>
        <h1>Go Tripping</h1>
      </div>

      {/* Home button */}
      <Link to="/" style={{ ...buttonStyle, left: '20px' }}>
        Home
      </Link>

      {/* Main content */}
      <div style={{ padding: '2rem', textAlign: 'center', marginTop: '220px' }}>
        <h2>Nearby Fun Places</h2>
        {selectedPlace ? (
          <div style={{ padding: '1rem' }}>
            <h3>{selectedPlace.name}</h3>
            <p>{selectedPlace.description}</p>
            <button onClick={() => setSelectedPlace(null)} style={{ padding: '6px 12px' }}>Back to list</button>
          </div>
        ) : (
          <ul style={{ listStyle: 'none', padding: 0 }}>
            {places.map((place) => (
              <li key={place.id} style={{ margin: '1rem 0' }}>
                <button
                  onClick={() => setSelectedPlace(place)}
                  style={{ padding: '8px 16px', cursor: 'pointer', borderRadius: '4px' }}
                >
                  {place.name}
                </button>
              </li>
            ))}
          </ul>
        )}
      </div>
    </div>
  );
}

export default Explore;
