import React, { useState } from 'react';
import './App.css';
import Login from './Login';
import Register from './Register';
import Explore from './Explore'; // <-- We'll make this

function App() {
  const [activePage, setActivePage] = useState<'home' | 'explore'>('home');
  const [activeForm, setActiveForm] = useState<'login' | 'register' | null>(null);

  const goHome = () => {
    setActivePage('home');
    setActiveForm(null);
  };
  const goExplore = () => {
    setActivePage('explore');
    setActiveForm(null);
  };

  return (
    <div className="App" style={{ backgroundColor: '#ffffff', minHeight: '100vh' }}>
      {/* Header */}
      <header style={{ backgroundColor: '#ff69b4', color: '#fff', padding: '1.5rem', borderRadius: '10px', textAlign: 'center' }}>
        <h1>Go Tripping</h1>
        <p>Explore top destinations near you and chat with fellow travelers!</p>
      </header>

      {/* Nav Buttons */}
      <div style={{ display: 'flex', justifyContent: 'space-between', padding: '1rem 2rem 0 2rem' }}>
        {/* Left */}
        <button onClick={goHome} style={buttonStyle}>Home</button>
        {/* Right */}
        <div>
          <button onClick={goExplore} style={{ ...buttonStyle, marginRight: '10px' }}>Explore</button>
          <button onClick={() => setActiveForm('login')} style={{ ...buttonStyle, marginRight: '10px' }}>Login</button>
          <button onClick={() => setActiveForm('register')} style={buttonStyle}>Register</button>
        </div>
      </div>

      {/* Main */}
      <main style={{ padding: '2rem', textAlign: 'center' }}>
        {activePage === 'home' && !activeForm && (
          <>
            <h2>Welcome to Go Tripping</h2>
            <p>Start by selecting a province and a radius to find interesting spots near you.</p>
          </>
        )}

        {activePage === 'explore' && <Explore />}

        {activeForm === 'login' && (
          <>
            <Login />
            <BackCloseRow goHome={goHome} />
          </>
        )}
        {activeForm === 'register' && (
          <>
            <Register />
            <BackCloseRow goHome={goHome} />
          </>
        )}
      </main>
    </div>
  );
}

// Shared style
const buttonStyle: React.CSSProperties = {
  backgroundColor: '#ffffff',
  color: '#000',
  border: '2px solid black',
  padding: '8px 16px',
  borderRadius: '5px',
  fontWeight: 'bold',
  cursor: 'pointer',
};

function BackCloseRow({ goHome }: { goHome: () => void }) {
  return (
    <div style={{ marginTop: '20px' }}>
      <button onClick={goHome} style={{ ...buttonStyle, marginRight: '10px' }}>Back</button>
      <button onClick={goHome} style={buttonStyle}>Close</button>
    </div>
  );
}

export default App;
