import React from 'react';
import { Link } from 'react-router-dom';

const communities = [
  { category: 'Age', name: 'Young Adults', description: 'Community for 18-29 year olds' },
  { category: 'Age', name: 'Seniors', description: 'Community for those 60+' },
  { category: 'Gender', name: 'Women Travelers', description: 'Female-only discussion group' },
  { category: 'Occupation', name: 'Freelancers', description: 'Digital nomads and remote workers' },
  { category: 'Social Status', name: 'Veterans', description: 'Ex-service members sharing travel tips' },
  { category: 'Race', name: 'Indigenous Communities', description: 'For Indigenous people to connect' },
  { category: 'Topics of Interest', name: 'Backpacking', description: 'Backpacking and adventure lovers' },
];

function Community() {
  const buttonStyle: React.CSSProperties = {
    position: 'fixed',
    top: '180px', // Position below the navigation buttons
    background: 'linear-gradient(135deg, #ff6b6b 0%, #ee5a24 100%)',
    color: '#ffffff',
    border: 'none',
    borderRadius: '25px',
    padding: '12px 20px',
    fontWeight: '600',
    cursor: 'pointer',
    textDecoration: 'none',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    width: '80px',
    height: '45px',
    fontSize: '13px',
    zIndex: 998,
    boxShadow: '0 4px 15px rgba(0, 0, 0, 0.2)',
    transition: 'all 0.3s ease',
    textTransform: 'uppercase',
    letterSpacing: '0.5px',
  };

  return (
    <div style={{ position: 'relative' }}>
      {/* Go Tripping header with pink background */}
      <div style={{ 
        width: '100%', 
        backgroundColor: '#ff69b4',
        color: '#000',
        padding: '1.5rem',
        textAlign: 'center',
        position: 'fixed',
        top: 0,
        left: 0,
        zIndex: 1000
      }}>
        <h1>Go Tripping</h1>
      </div>

      {/* Home button */}
      <Link 
        to="/" 
        style={{ ...buttonStyle, left: '20px' }}
        onMouseEnter={(e) => {
          e.currentTarget.style.transform = 'translateY(-2px)';
          e.currentTarget.style.boxShadow = '0 6px 20px rgba(0, 0, 0, 0.3)';
          e.currentTarget.style.background = 'linear-gradient(135deg, #ee5a24 0%, #ff6b6b 100%)';
        }}
        onMouseLeave={(e) => {
          e.currentTarget.style.transform = 'translateY(0)';
          e.currentTarget.style.boxShadow = '0 4px 15px rgba(0, 0, 0, 0.2)';
          e.currentTarget.style.background = 'linear-gradient(135deg, #ff6b6b 0%, #ee5a24 100%)';
        }}
      >
        Home
      </Link>

      {/* Main content */}
      <div style={{ padding: '2rem', textAlign: 'center', marginTop: '220px' }}>
        <h2>Join a Community</h2>
        <ul style={{ listStyle: 'none', padding: 0 }}>
          {communities.map((comm, index) => (
            <li
              key={index}
              style={{
                backgroundColor: '#f0f0f0',
                padding: '1rem',
                borderRadius: '8px',
                margin: '1rem auto',
                maxWidth: '400px',
              }}
            >
              <h3>{comm.name}</h3>
              <p><strong>Category:</strong> {comm.category}</p>
              <p>{comm.description}</p>
              <button style={{ padding: '6px 12px', cursor: 'pointer' }}>Join</button>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
}

export default Community;
