import React, { useState } from 'react';
import './App.css';
import logo from './logo.svg';
import Login from './Login';
import Register from './Register';

function App() {
  const [activeForm, setActiveForm] = useState<'login' | 'register' | null>(null);

  const goHome = () => setActiveForm(null); // reset to home view

  return (
    <div className="App" style={{ backgroundColor: '#ffffff', minHeight: '100vh' }}>
      {/* Header Banner */}
      <header
        style={{
          backgroundColor: '#ff69b4',
          color: '#fff',
          padding: '1.5rem',
          borderRadius: '10px',
          textAlign: 'center',
        }}
      >
        <h1 style={{ margin: 0 }}>Go Tripping</h1>
        <p style={{ marginTop: '0.5rem' }}>
          Explore top destinations near you and chat with fellow travelers!
        </p>
      </header>

      {/* Top Navigation Row: Home (left) and Login/Register (right) */}
      <div
        style={{
          display: 'flex',
          justifyContent: 'space-between',
          padding: '1rem 2rem 0 2rem',
        }}
      >
        {/* Home Button */}
        <button
          onClick={goHome}
          style={{
            backgroundColor: '#ffffff',
            color: '#000',
            border: '2px solid black',
            padding: '8px 16px',
            borderRadius: '5px',
            fontWeight: 'bold',
            cursor: 'pointer',
          }}
        >
          Home
        </button>

        {/* Login & Register Buttons */}
        <div>
          <button
            onClick={() => setActiveForm('login')}
            style={{
              backgroundColor: '#ffffff',
              color: '#000000',
              border: '2px solid black',
              padding: '8px 16px',
              marginRight: '10px',
              cursor: 'pointer',
              borderRadius: '5px',
              fontWeight: 'bold',
            }}
          >
            Login
          </button>
          <button
            onClick={() => setActiveForm('register')}
            style={{
              backgroundColor: '#ffffff',
              color: '#000000',
              border: '2px solid black',
              padding: '8px 16px',
              cursor: 'pointer',
              borderRadius: '5px',
              fontWeight: 'bold',
            }}
          >
            Register
          </button>
        </div>
      </div>

      {/* Main Body */}
      <main style={{ padding: '2rem', textAlign: 'center' }}>
        {activeForm === null && (
          <>
            <h2>Welcome to Go Tripping</h2>
            <p>
              Start by selecting a province and a radius to find interesting spots near you.
            </p>
          </>
        )}

        {/* Login or Register Form Display */}
        {activeForm === 'login' && (
          <>
            <Login />
            <div style={{ marginTop: '20px' }}>
              <button onClick={goHome} style={backButtonStyle}>Back</button>
              <button onClick={goHome} style={closeButtonStyle}>Close</button>
            </div>
          </>
        )}
        {activeForm === 'register' && (
          <>
            <Register />
            <div style={{ marginTop: '20px' }}>
              <button onClick={goHome} style={backButtonStyle}>Back</button>
              <button onClick={goHome} style={closeButtonStyle}>Close</button>
            </div>
          </>
        )}
      </main>
    </div>
  );
}

// Reusable button styles
const backButtonStyle = {
  backgroundColor: '#dddddd',
  color: '#000',
  border: '1px solid gray',
  padding: '8px 16px',
  marginRight: '10px',
  cursor: 'pointer',
  borderRadius: '5px',
};

const closeButtonStyle = {
  backgroundColor: '#ffdddd',
  color: '#000',
  border: '1px solid gray',
  padding: '8px 16px',
  cursor: 'pointer',
  borderRadius: '5px',
};

export default App;
