import React from 'react';

const communities = [
  { category: 'Age', name: 'Young Adults', description: 'Community for 18-29 year olds' },
  { category: 'Age', name: 'Seniors', description: 'Community for those 60+' },
  { category: 'Gender', name: 'Women Travelers', description: 'Female-only discussion group' },
  { category: 'Occupation', name: 'Freelancers', description: 'Digital nomads and remote workers' },
  { category: 'Social Status', name: 'Veterans', description: 'Ex-service members sharing travel tips' },
  { category: 'Race', name: 'Indigenous Communities', description: 'For Indigenous people to connect' },
  { category: 'Topics of Interest', name: 'Backpacking', description: 'Backpacking and adventure lovers' },
];

function Community() {
  return (
    <div>
      <h2>Join a Community</h2>
      <ul style={{ listStyle: 'none', padding: 0 }}>
        {communities.map((comm, index) => (
          <li
            key={index}
            style={{
              backgroundColor: '#f0f0f0',
              padding: '1rem',
              borderRadius: '8px',
              margin: '1rem auto',
              maxWidth: '400px',
            }}
          >
            <h3>{comm.name}</h3>
            <p><strong>Category:</strong> {comm.category}</p>
            <p>{comm.description}</p>
            <button style={{ padding: '6px 12px', cursor: 'pointer' }}>Join</button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default Community;
