import React from 'react';
import { Link } from 'react-router-dom';

type UserComment = {
  id: number;
  name: string;
  comment: string;
};

const sampleUsers: UserComment[] = [
  { id: 1, name: 'Alice', comment: 'I loved the Beach Paradise!' },
  { id: 2, name: 'John', comment: 'Mountain Retreat was amazing!' },
  { id: 3, name: 'Sophia', comment: 'Historic City has the best food!' },
];

function People() {
  const buttonStyle: React.CSSProperties = {
    position: 'fixed',
    top: '180px', // Position below the navigation buttons
    background: 'linear-gradient(135deg, #ff6b6b 0%, #ee5a24 100%)',
    color: '#ffffff',
    border: 'none',
    borderRadius: '25px',
    padding: '12px 20px',
    fontWeight: '600',
    cursor: 'pointer',
    textDecoration: 'none',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    width: '80px',
    height: '45px',
    fontSize: '13px',
    zIndex: 998,
    boxShadow: '0 4px 15px rgba(0, 0, 0, 0.2)',
    transition: 'all 0.3s ease',
    textTransform: 'uppercase',
    letterSpacing: '0.5px',
  };

  return (
    <div style={{ position: 'relative' }}>
      {/* Go Tripping header with pink background */}
      <div style={{ 
        width: '100%', 
        backgroundColor: '#ff69b4',
        color: '#000',
        padding: '1.5rem',
        textAlign: 'center',
        position: 'fixed',
        top: 0,
        left: 0,
        zIndex: 1000
      }}>
        <h1>Go Tripping</h1>
      </div>

      {/* Home button */}
      <Link 
        to="/" 
        style={{ ...buttonStyle, left: '20px' }}
        onMouseEnter={(e) => {
          e.currentTarget.style.transform = 'translateY(-2px)';
          e.currentTarget.style.boxShadow = '0 6px 20px rgba(0, 0, 0, 0.3)';
          e.currentTarget.style.background = 'linear-gradient(135deg, #ee5a24 0%, #ff6b6b 100%)';
        }}
        onMouseLeave={(e) => {
          e.currentTarget.style.transform = 'translateY(0)';
          e.currentTarget.style.boxShadow = '0 4px 15px rgba(0, 0, 0, 0.2)';
          e.currentTarget.style.background = 'linear-gradient(135deg, #ff6b6b 0%, #ee5a24 100%)';
        }}
      >
        Home
      </Link>

      {/* Main content */}
      <div style={{ padding: '2rem', textAlign: 'center', marginTop: '220px' }}>
        <h2>Our Travelers' Reviews</h2>
        <ul style={{ listStyle: 'none', padding: 0 }}>
          {sampleUsers.map((user) => (
            <li
              key={user.id}
              style={{
                background: '#f0f0f0',
                padding: '1rem',
                margin: '1rem 0',
                borderRadius: '8px',
                maxWidth: '400px',
                marginLeft: 'auto',
                marginRight: 'auto',
              }}
            >
              <h3>{user.name}</h3>
              <p>{user.comment}</p>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
}

export default People;
