import React from 'react';
import { Link } from 'react-router-dom';

function Home() {
  const homeButtonStyle: React.CSSProperties = {
    position: 'absolute',
    top: '120px', // Position below the pink bar
    left: '20px',
    backgroundColor: '#ffffff',
    color: '#000',
    border: '2px solid black',
    borderRadius: '50%', // Makes it circular
    padding: '12px 16px',
    fontWeight: 'bold',
    cursor: 'pointer',
    textDecoration: 'none',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    width: '60px',
    height: '60px',
    fontSize: '14px',
  };

  return (
    <div style={{ position: 'relative' }}>
      {/* Pink color bar */}
      <div style={{ 
        width: '100%', 
        height: '96px', // 1 inch = 96px 
        backgroundColor: '#ff69b4',
        position: 'absolute',
        top: 0,
        left: 0,
        zIndex: 1
      }}></div>
      
      {/* Home button */}
      <Link to="/" style={homeButtonStyle}>
        Home
      </Link>
      
      {/* Main content */}
      <div style={{ padding: '2rem', textAlign: 'center', marginTop: '120px' }}>
        <h2>Welcome to Go Tripping</h2>
        <p>Start by selecting a province and a radius to find interesting spots near you.</p>
      </div>
    </div>
  );
}

export default Home;
