import React from 'react';

type UserComment = {
  id: number;
  name: string;
  comment: string;
};

const sampleUsers: UserComment[] = [
  { id: 1, name: 'Alice', comment: 'I loved the Beach Paradise!' },
  { id: 2, name: 'John', comment: 'Mountain Retreat was amazing!' },
  { id: 3, name: 'Sophia', comment: 'Historic City has the best food!' },
];

function People() {
  return (
    <div style={{ textAlign: 'center' }}>
      <h2>Our Travelersâ€™ Reviews</h2>
      <ul style={{ listStyle: 'none', padding: 0 }}>
        {sampleUsers.map((user) => (
          <li
            key={user.id}
            style={{
              background: '#f0f0f0',
              padding: '1rem',
              margin: '1rem 0',
              borderRadius: '8px',
              maxWidth: '400px',
              marginLeft: 'auto',
              marginRight: 'auto',
            }}
          >
            <h3>{user.name}</h3>
            <p>{user.comment}</p>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default People;
