import React from 'react';
import { Link } from 'react-router-dom';

function Home() {
  const buttonStyle: React.CSSProperties = {
    position: 'fixed',
    top: '180px', // Position below the navigation buttons
    backgroundColor: '#ffffff',
    color: '#000',
    border: '2px solid black',
    borderRadius: '50%', // Makes it circular
    padding: '12px 16px',
    fontWeight: 'bold',
    cursor: 'pointer',
    textDecoration: 'none',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    width: '60px',
    height: '60px',
    fontSize: '14px',
    zIndex: 998,
  };

  return (
    <div style={{ position: 'relative' }}>
      {/* Go Tripping header with pink background */}
      <div style={{ 
        width: '100%', 
        backgroundColor: '#ff69b4',
        color: '#000',
        padding: '1.5rem',
        textAlign: 'center',
        position: 'fixed',
        top: 0,
        left: 0,
        zIndex: 1000
      }}>
        <h1>Go Tripping</h1>
      </div>
      
      {/* Home button */}
      <Link to="/" style={{ ...buttonStyle, left: '20px' }}>
        Home
      </Link>
      
      {/* Spots button */}
      <Link to="/spots" style={{ ...buttonStyle, left: '100px' }}>
        Spots
      </Link>
      
      {/* Main content */}
      <div style={{ padding: '2rem', textAlign: 'center', marginTop: '220px' }}>
        <h2>Welcome to Go Tripping</h2>
        <p>Start by selecting a province and a radius to find interesting spots near you.</p>
      </div>
    </div>
  );
}

export default Home;
