import React, { useState } from 'react';
import { Link } from 'react-router-dom';

function Register() {
  const [email, setEmail] = useState('');
  const [name, setName] = useState('');
  const [password, setPassword] = useState('');

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    alert(`Registering:\nName: ${name}\nEmail: ${email}`);
    // You can connect this to your backend here
  };

  const buttonStyle: React.CSSProperties = {
    backgroundColor: '#ffffff',
    color: '#000',
    border: '2px solid black',
    padding: '8px 16px',
    borderRadius: '5px',
    fontWeight: 'bold',
    cursor: 'pointer',
    textDecoration: 'none',
    display: 'inline-block',
    marginTop: '20px',
    marginRight: '10px',
  };

  return (
    <div style={{ padding: '2rem', textAlign: 'center' }}>
      <div style={{ maxWidth: '400px', margin: 'auto', padding: '2rem', backgroundColor: '#f5f5f5', borderRadius: '8px' }}>
        <h2 style={{ textAlign: 'center', marginBottom: '2rem' }}>Create Account</h2>
        <form onSubmit={handleSubmit}>
          <input
            type="text"
            placeholder="Full Name"
            value={name}
            onChange={(e) => setName(e.target.value)}
            required
            style={{
              display: 'block',
              width: '100%',
              padding: '10px',
              marginBottom: '1rem',
              border: '1px solid #ccc',
              borderRadius: '4px',
            }}
          />
          <input
            type="email"
            placeholder="Email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
            style={{
              display: 'block',
              width: '100%',
              padding: '10px',
              marginBottom: '1rem',
              border: '1px solid #ccc',
              borderRadius: '4px',
            }}
          />
          <input
            type="password"
            placeholder="Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
            style={{
              display: 'block',
              width: '100%',
              padding: '10px',
              marginBottom: '1rem',
              border: '1px solid #ccc',
              borderRadius: '4px',
            }}
          />
          <button
            type="submit"
            style={{
              width: '100%',
              padding: '10px',
              backgroundColor: '#61dafb',
              border: 'none',
              borderRadius: '4px',
              cursor: 'pointer',
              fontWeight: 'bold',
            }}
          >
            Register
          </button>
        </form>
        
        <div style={{ marginTop: '2rem', textAlign: 'center' }}>
          <p>Already have an account? <Link to="/login" style={{ color: '#61dafb', textDecoration: 'none' }}>Login here</Link></p>
        </div>
      </div>
    </div>
  );
}

export default Register;
