import React, { useState } from 'react';
import './App.css';
import logo from './logo.svg';
import Login from './Login';
import Register from './Register';

function App() {
  const [activeForm, setActiveForm] = useState<'login' | 'register' | null>(null);

  return (
    <div className="App" style={{ backgroundColor: '#ffffff', minHeight: '100vh' }}>
      {/* Pink Header */}
      <header
        style={{
          backgroundColor: '#ff69b4',
          color: '#fff',
          padding: '1.5rem',
          borderRadius: '10px',
          textAlign: 'center',
        }}
      >
        <h1 style={{ margin: 0 }}>Go Tripping</h1>
        <p style={{ marginTop: '0.5rem' }}>
          Explore top destinations near you and chat with fellow travelers!
        </p>
      </header>

      {/* Top-right Login/Register Buttons */}
      <div
        style={{
          display: 'flex',
          justifyContent: 'flex-end',
          padding: '1rem 2rem 0 2rem',
        }}
      >
        <button
          onClick={() => setActiveForm('login')}
          style={{
            backgroundColor: '#ffffff',
            color: '#000000',
            border: '2px solid black',
            padding: '8px 16px',
            marginRight: '10px',
            cursor: 'pointer',
            borderRadius: '5px',
            fontWeight: 'bold',
          }}
        >
          Login
        </button>
        <button
          onClick={() => setActiveForm('register')}
          style={{
            backgroundColor: '#ffffff',
            color: '#000000',
            border: '2px solid black',
            padding: '8px 16px',
            cursor: 'pointer',
            borderRadius: '5px',
            fontWeight: 'bold',
          }}
        >
          Register
        </button>
      </div>

      {/* Welcome Section */}
      <main style={{ padding: '2rem', textAlign: 'center' }}>
        <h2>Welcome to Go Tripping</h2>
        <p>Start by selecting a province and a radius to find interesting spots near you.</p>

        {/* Conditionally Render Forms */}
        <div style={{ marginTop: '2rem' }}>
          {activeForm === 'login' && <Login />}
          {activeForm === 'register' && <Register />}
        </div>
      </main>
    </div>
  );
}

export default App;
