import React, { useState } from 'react';
import { Link } from 'react-router-dom';

function Login() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    alert(`Logging in with email: ${email}\nPassword: ${password}`);
    // You can replace this with actual API logic
  };

  const buttonStyle: React.CSSProperties = {
    position: 'fixed',
    top: '180px', // Position below the navigation buttons
    backgroundColor: '#ffffff',
    color: '#000',
    border: '2px solid black',
    borderRadius: '50%', // Makes it circular
    padding: '12px 16px',
    fontWeight: 'bold',
    cursor: 'pointer',
    textDecoration: 'none',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    width: '60px',
    height: '60px',
    fontSize: '14px',
    zIndex: 998,
  };

  return (
    <div style={{ position: 'relative' }}>
      {/* Go Tripping header with pink background */}
      <div style={{ 
        width: '100%', 
        backgroundColor: '#ff69b4',
        color: '#000',
        padding: '1.5rem',
        textAlign: 'center',
        position: 'fixed',
        top: 0,
        left: 0,
        zIndex: 1000
      }}>
        <h1>Go Tripping</h1>
      </div>
      
      {/* Home button */}
      <Link to="/" style={{ ...buttonStyle, left: '20px' }}>
        Home
      </Link>
      
      {/* Main content */}
      <div style={{ padding: '2rem', textAlign: 'center', marginTop: '220px' }}>
        <div style={{ maxWidth: '400px', margin: 'auto', padding: '2rem', backgroundColor: '#f5f5f5', borderRadius: '8px' }}>
          <h2 style={{ textAlign: 'center', marginBottom: '2rem' }}>Welcome Back</h2>
          <form onSubmit={handleSubmit}>
            <input
              type="email"
              placeholder="Email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
              style={{
                display: 'block',
                width: '100%',
                padding: '10px',
                marginBottom: '1rem',
                border: '1px solid #ccc',
                borderRadius: '4px',
              }}
            />
            <input
              type="password"
              placeholder="Password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
              style={{
                display: 'block',
                width: '100%',
                padding: '10px',
                marginBottom: '1rem',
                border: '1px solid #ccc',
                borderRadius: '4px',
              }}
            />
            <button
              type="submit"
              style={{
                width: '100%',
                padding: '10px',
                backgroundColor: '#61dafb',
                border: 'none',
                borderRadius: '4px',
                cursor: 'pointer',
                fontWeight: 'bold',
              }}
            >
              Login
            </button>
          </form>
          
          <div style={{ marginTop: '2rem', textAlign: 'center' }}>
            <p>Don't have an account? <Link to="/register" style={{ color: '#61dafb', textDecoration: 'none' }}>Register here</Link></p>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Login;
